import React from 'react'
import Button from '../Button/Button'
import classes from './UsersList.css'

var renderOutput = [];

export function UsersList(props){
    renderOutput = [];
    console.log(props.onClick);
    for( var i = 0; i < props.length; i++ ){
        if(props[i].id === localStorage.getItem('userId')){
            renderOutput.push(<div key = {props[i].id} > <b>{props[i].name}</b> </div>);
        } else {
            renderOutput.push(
                <div key = {props[i].id} > 
                    {props[i].name} 
                    {props[i].ready ? <Button type='success' id={props[i].id} onClick={props.onClick}>Играть</Button> : null}
                </div>);
        }
    }
}

export function NewUser(props){
    renderOutput.push(
        <div key = {props.id} > 
            {props.name} 
            {props.ready ? <Button type='success' id={props.id} onClick={props.onClick}>Играть</Button> : null}
        </div>);
}

export function UserDisconnect(props){
    for( var i = 0; i < renderOutput.length; i++){ 
        if ( renderOutput[i].key === props.id ) {
            renderOutput.splice(i, 1); 
        }
    }
}

export function UserReady(props){
    for( var i = 0; i < renderOutput.length; i++){ 
        if ( renderOutput[i].key === props.id ) {
            renderOutput[i] = 
                <div key = {props.id} > 
                    {props.name} 
                    {props.ready ? <Button type='success' id={props.id} onClick={props.onClick}>Играть</Button> : null}
                </div>; 
        }
    }
}

const Users = props =>{
    return(
        <div>
            Users online: {props.Count}
            <div className={classes.UsersList}>                                
                {renderOutput}
            </div>
        </div> 
    ) 
}

export default Users;
