import {Component} from 'react'

import {history} from '../store/history'

import { hubConnection } from 'signalr-no-jquery';

import {connect} from 'react-redux'

import {getUserProfile,
    onConnected,
    onNewUserConnected,
    onUserDisconnected,
    onUserReady} from '../store/actions/playTable'

export const connection = hubConnection('http://localhost:3001'), playHubProxy = connection.createHubProxy('playHub');

class PlayConnection extends Component{
    componentDidMount(){
        const userId = localStorage.getItem('userId');
        
        this.props.getUserProfile(userId);

        playHubProxy.on('onGameStart', function(user, enemy, messages, firstId){
            this.setState({
                cash: user.cash,
                bet: user.bet,
                enemyName: enemy.name,
                enemyCash: enemy.cash,
                enemyBet: enemy.bet,
                messages: messages
            });
            if(firstId == userId){
                this.playOffer();
            }
            scrollDown();
        }.bind(this));

        playHubProxy.on('onEnemyBet', function(enemy, dibsBet){
            var tmp = document.createElement('div');
            tmp.innerHTML = dibsBet;
            document.getElementById('enemyDibsBet').appendChild(tmp);
            this.setState({
                enemyCash: enemy.cash,
                enemyBet: enemy.bet
            });
        }.bind(this));

        playHubProxy.on('onBet', function(user){                  
            this.setState({                    
                cash: user.cash,
                bet: user.bet
            });
        }.bind(this));

        playHubProxy.on('onStopGame', function(){
            this.props.history.push('/profile');
        }.bind(this));

        playHubProxy.on('onMessage', function(message){
            this.setState({
                messages: [...this.state.messages, message]
            });
            scrollDown();
        }.bind(this));

        playHubProxy.on('onPlayOffer', function(userId){
            if(this.state.bet == this.state.enemyBet && this.state.bet > 0 && this.state.firstBet){
                this.setState({
                    isBet: false,
                    firstBet: false,
                    enableBet: false
                });
                this.props.onPlayWithUserHandler();
            } else if(userId == localStorage.getItem('userId')){
                this.setState({
                    enableBet: false,
                    isRaise: false,
                    isCheck: false,
                    isFold: false
                });
            } else{
                this.setState({
                    enableBet: true,
                    isFold: true,
                    isCheck: this.state.enemyBet > 0
                });
            }
        }.bind(this));

        playHubProxy.on('onTimer', function(time){
            this.setState({
                time: time,
                timer: true
            });
        }.bind(this));            

        if(enemyId != -1){
            connection.start().done(function(){
                console.log('start game');
                playHubProxy.invoke('gameStart', userId);
            }.bind(this));
        }
    }

    componentWillUnmount(){
        playHubProxy.off('onGameStart');
        playHubProxy.off('onEnemyBet');
        playHubProxy.off('onBet');
        playHubProxy.off('onStopGame');
        playHubProxy.off('onMessage');
        playHubProxy.off('onPlayOffer');
        playHubProxy.off('onTimer');

        connection.stop();
    }

    render(){
        return null;
    }
}

function mapStateToProps(state) {
    const { count, users, user } = state.playTable;
    return {
        count, users, user
    };
}

function mapDispatchToProps(dispatch){
    return{
        getUserProfile: (userId) => dispatch(getUserProfile(userId)),
        onConnected: (profiles) => dispatch(onConnected(profiles)),
        onNewUserConnected: (newUser) =>dispatch(onNewUserConnected(newUser)),
        onUserDisconnected: (profile) =>dispatch(onUserDisconnected(profile)),
        onUserReady: (profile) =>dispatch(onUserReady(profile))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(PlayConnection)
