import React, {Component} from 'react'
import classes from './Profile.css'
import {NavLink, Redirect} from 'react-router-dom'
import Button from '../../components/UI/Button/Button'
import axios from 'axios';

import $ from 'jquery';
import StartConnection, {connection, playHubProxy} from '../../Hubs/Hubs'
//import { hubConnection } from 'signalr-no-jquery';

var renderOutput = [];
function UsersList(props) {
    renderOutput = [];
    for( var i = 0; i < props.length; i++ ){
        if(props[i].id === localStorage.getItem('userId')){
            renderOutput.push(<b key = {props[i].id} > {props[i].name} </b>);
        } else {
            renderOutput.push(<div key = {props[i].id} > {props[i].name} <button>Играть</button> </div>);
        }
    }
    console.log("output")
    console.log(renderOutput);
}
function NewUser(props) {
    renderOutput.push(<div key = {props.id} > {props.name} <button>Играть</button> </div>);
    console.log("output")
    console.log(renderOutput);
}
function UserDisconnect (props) {
    for( var i = 0; i < renderOutput.length; i++){ 
        if ( renderOutput[i].key === props.id ) {
            renderOutput.splice(i, 1); 
        }
     }
    console.log("output")
    console.log(renderOutput);
}

var setCount;

class Profile extends Component{
    state = {
        isLogout: false,
        bet: '',
        name: '',
        email: '',
        count: 0
    }

    isLogout = () => {
        localStorage.removeItem('userId');
        this.setState({
            isLogout: true
        });        
        console.log('stop');
        connection.stop();
    }

    componentDidMount(){
        const userId = localStorage.getItem('userId');
        this.getDataUser(userId).then(() => {
            StartConnection("connect", userId);
        });

        function sss(){
            console.log(this.state.name);
            console.log(this.state.count);
            this.setState({ //--------------Can't call setState on an unmounted component
                count: setCount
            });           
            console.log(this.state.count);
        };
        sss = sss.bind(this);

        playHubProxy.on('onConnected', function(profiles){
            console.log('Connected')
            UsersList(profiles);
            console.log(profiles);
            setCount = profiles.length;
            sss();            
        });

        playHubProxy.on('onNewUserConnected', function(newUser){
            console.log('New user')
            console.log(newUser);
            setCount++;
            NewUser(newUser);
            sss();
            console.log(setCount);
        });
        playHubProxy.on('onUserDisconnected', function(profile){
            console.log('User disconnected')
            console.log(profile);
            setCount--;
            UserDisconnect(profile);
            sss();
            console.log(setCount);
        });
        /*
        if(connection.state == 4){
            /*connection.start().done(function(){
                playHubProxy.invoke('connect', userId);
                console.log(userId);
            });*//*
            StartConnection("connect", userId);
        } else {
            sss();
        }*/
    }

    getDataUser = async (userId)=>{
        const data = {
            userId: userId  
        }
        const respons = await axios.post('http://localhost:3001/profile', data);
        if(respons.data){
            this.setState({
                isLogout: false,
                bet: respons.data.bet,
                name: respons.data.name,
                email: respons.data.email
            })
        } else this.setState({isLogout: true});
    }

    render(){
        if(this.state.isLogout){
            return (<Redirect to='/' />)
        }else{
            return(
                <div className={classes.Profile}>
                    <div>
                        <h1>{this.state.name}</h1>
                        <hr />
                        <p>
                            <b>Почта: </b><em>{this.state.email}</em><br />
                            <b>Счет: </b><em>{this.state.bet}</em><br />
                        </p>
                        <hr />
                        <div className={classes.Buttons}>
                            <NavLink to="/play">
                                <Button 
                                    type="success" 
                                >Играть</Button>
                            </NavLink> 
                            <span id="disconnect"><Button
                                type="error"
                                onClick={this.isLogout} 
                            >Выход</Button></span>  
                        </div>  
                        <div>
                            Users online: {this.state.count}
                            <div>                                
                                {renderOutput}
                            </div>
                        </div>                         
                    </div>        
                </div>
            )   
        }

    }
}

export default Profile
