import {FETCH_PLAY_START,
        FETCH_MAKE_BET,
        HAND_SUCCESS,
        WIN_GAME,
        LOSE_GAME,
        DRAW_GAME,
        DEAL_HAND,
        PLAY_HAND,
        USER_PROFILE,
        DATA_USER,
        ENOUGH_HAND,
        USER_CONNECT,
        NEW_USER_CONNECT,
        USER_DISCONNECT,
        USER_READY} from './actionType'
import axios from 'axios'

export function onConnected(profiles){
    return {
        type: USER_CONNECT,
        profiles
    }
}

export function onNewUserConnected(newUser){
    return {
        type: NEW_USER_CONNECT,
        newUser
    }
}

export function onUserDisconnected(profile){
    return {
        type: USER_DISCONNECT,
        profile
    }
}

export function onUserReady(profile){
    return {
        type: USER_READY,
        profile
    }
}

export function fetchMakeBet(bet, cash, isPlay){
    return {
        type: FETCH_MAKE_BET,
        bet, cash, isPlay
    }
}

export function getUserProfile(userId){
    return async dispatch =>{
        const data = {
            userId: userId  
        }
        let setStateUser;
        const respons = await axios.post('http://localhost:3001/profile', data);
        if(respons.data){
            setStateUser = {
                id: respons.data.id,
                cash: respons.data.bet,
                name: respons.data.name,
                email: respons.data.email
            }
        }

        dispatch(userProfile(setStateUser));
    }
}

export function getDataUser(userId){
    return async dispatch =>{
        const data = {
            userId: userId  
        }
        let setStateUser;
        const respons = await axios.post('http://localhost:3001/play', data);
        if(respons.data){
            setStateUser = {
                bet: 0,
                cash: respons.data.bet,
                name: respons.data.name,
                isPlay: true
            }
        }

        dispatch(dataUser(setStateUser));
    }

}

export function onPlayWithUserHandler (){
    return async (dispatch, getState) => {
        const state = getState().playTable;
        let playerHand = await [getCard(state), getCard(state)];
        let playerHandSum = await getSum(playerHand);

        const set_state = {
            playerHand,
            playerHandSum,
            isPlay: false,
            isEnough: true,
            isMore: true
        };

        dispatch(handSuccess(set_state));
    }
}

export function onMoreWithUserHandler(){
    return async (dispatch, getState) =>{
        const state = getState().playTable;
        let playerHand = state.user.playerHand;
        playerHand.push(getCard(state));
        let playerHandSum = await getSum(playerHand);
        const play_setState = {
            playerHand,
            playerHandSum
        }
        dispatch(playHand(play_setState));
    }
}

export function onEnoughWithUserHandler(){
    return async (dispatch, getState) => {
        const state = getState().playTable;
        const set_state = {
            isPlay: false,
            isEnough: false,
            isMore: false
        };

        dispatch(enoughHand(set_state));
    }
}

export function onPlayHandler (){
    return async (dispatch, getState) => {
        const state = getState().playTable;
        let playerHand = await [getCard(state), getCard(state)];
        let dealerHand = await [getCard(state)];
        let playerHandSum = await getSum(playerHand);
        let dealerHandSum = await getSum(dealerHand);

        const set_state = {
            playerHand,
            playerHandSum,
            dealerHand,
            dealerHandSum,
            isPlay: false,
            isEnough: true,
            isMore: true
        };

        dispatch(handSuccess(set_state));

        if(playerHandSum === 21){ 
            setTimeout(()=>{
                let cash = state.user.cash + state.user.bet * 2;
                const win_setState = {
                    playerHandSum: 0,
                    dealerHandSum: 0,
                    bet: 0,
                    cash,
                    playerHand:[],
                    dealerHand:[],
                    isEnough: false,
                    isMore: false
                };
                updateData(cash);
                dispatch(winGame(win_setState)); 
                onDeletDib();  
                alert('У Вас BlackJack!!!!!!!!!!!!!'); 
            }, 600);           

        }else if(playerHandSum > 21){
            setTimeout(()=>{
                let cash = state.user.cash;
                alert('Вы проиграли!!!!!!!');
                onDeletDib();
                const lose_setState = {
                    playerHandSum: 0,
                    bet: 0,
                    dealerHandSum: 0,
                    playerHand:[],
                    dealerHand:[],
                    isEnough: false,
                    isMore: false
                };
                updateData(cash);
                dispatch(loseGame(lose_setState));  
            }, 600);

        }   
    }    
}

