            isPlay: isPlay
        }
        dispatch(dataUser(setStateUser));
    }

}

export function onPlayHandler (){
    return async (dispatch, getState) => {
        const state = getState().playTable;
        let playerHand = await [getCard(state), getCard(state)];
        let dealerHand = await [getCard(state)];
        let playerHandSum = await getSum(playerHand);
        let dealerHandSum = await getSum(dealerHand);

        const set_state = {
            playerHand,
            playerHandSum,
            dealerHand,
            dealerHandSum,
            isPlay: false,
            isEnough: true,
            isMore: true
        };

        dispatch(handSuccess(set_state));

        if(playerHandSum === 21){ 
            setTimeout(()=>{
                let cash = state.cash + state.bet*2;
                const win_setState = {
                    playerHandSum: 0,
                    dealerHandSum: 0,
                    bet: 0,
                    cash,
                    playerHand:[],
                    dealerHand:[],
                    isEnough: false,
                    isMore: false
                };
                updateData(cash);
                dispatch(winGame(win_setState)); 
                onDeletDib();  
                alert('У Вас BlackJack!!!!!!!!!!!!!'); 
            }, 600);           

        }else if(playerHandSum > 21){
            setTimeout(()=>{
                let cash = state.cash;
                alert('Вы проиграли!!!!!!!');
                onDeletDib();
                const lose_setState = {
                    playerHandSum: 0,
                    bet: 0,
                    dealerHandSum: 0,
                    playerHand:[],
                    dealerHand:[],
                    isEnough: false,
                    isMore: false
                };
                updateData(cash);
                dispatch(loseGame(lose_setState));  
            }, 600);

        }   
    }    
}

export function onEnoughHandler(){
    return async (dispatch, getState) => {
        const state = getState().playTable;
        let dealerHand = state.dealerHand;
        await dealerHand.push(getCard(state));
        let dealerHandSum = await getSum(dealerHand);
        const deal_setState_first = {
            dealerHand,
            dealerHandSum
        }
        await dispatch(dealHand(deal_setState_first));

        while(dealerHandSum < 17){
            await dealerHand.push(getCard(state));
            dealerHandSum = await getSum(dealerHand);
            const deal_setState = {
                dealerHand,
                dealerHandSum
            }
            await dispatch(dealHand(deal_setState));
        }

        if(dealerHandSum === 21){
            setTimeout(()=>{
                let cash = state.cash;
                const lose_setState = {
                    playerHandSum: 0,
                    bet: 0,
                    dealerHandSum: 0,
                    playerHand:[],
                    dealerHand:[],
                    isEnough: false,
                    isMore: false
                }; 
                dispatch(loseGame(lose_setState));
                onDeletDib(); 
                updateData(cash); 
                alert('У дилера BlackJack! Вы проиграли((((('); 
            }, 600); 
        }else if(dealerHandSum > 21 || state.playerHandSum > dealerHandSum){
            setTimeout(()=>{
                let cash = state.cash + state.bet*2;
                const win_setState = {
                    playerHandSum: 0,
                    dealerHandSum: 0,
                    bet: 0,
                    cash,
                    playerHand:[],
                    dealerHand:[],
                    isEnough: false,
                    isMore: false
                }; 
                dispatch(winGame(win_setState));
                onDeletDib();  
                updateData(cash);
                alert('Вы выграли!!!!!!!!!!!!!'); 
            }, 600);        
        }else if(dealerHandSum === state.playerHandSum){
            setTimeout(()=>{
                let cash = state.cash + state.bet;
                const draw_setState = {
                    playerHandSum: 0,
                    dealerHandSum: 0,
                    bet: 0,
                    cash,
                    playerHand:[],
                    dealerHand:[],
                    isEnough: false,
                    isMore: false
                }; 
                dispatch(drawGame(draw_setState));
                onDeletDib();  
                updateData(cash);
                alert('Победила дружба!!!!!!!!!!!!!'); 
            }, 600); 
        }else{
            setTimeout(()=>{
                let cash = state.cash;
                alert('Вы проиграли!!!!!!!');
                onDeletDib();
                const lose_setState = {
                    playerHandSum: 0,
                    bet: 0,
                    dealerHandSum: 0,
                    playerHand:[],
                    dealerHand:[],
                    isEnough: false,
                    isMore: false
                };
                updateData(cash);
                dispatch(loseGame(lose_setState));
            }, 600);
        }

    }
}

